import React, { useState } from 'react';
import '../styles/Header.css';
import logo from '../assets/logo.png';
import AboutModal from './AboutModal';

function Header() {
  const [aboutOpen, setAboutOpen] = useState(false);
  return (
    <header className="brand-header">
      <div className="brand-logo-block">
        <div className="brand-title-block">
          <h1 className="brand-title">ProThemesRU</h1>
          <div className="brand-slogan">Ваша платформа для быстрого создания сайтов</div>
        </div>
      </div>
      <div className="brand-cta-block">
        <h2 className="brand-cta">Создайте свой уникальный сайт за 5 минут!</h2>
        <a href="#canvas" className="brand-cta-btn">Начать бесплатно</a>
        <button className="brand-about-btn" onClick={() => setAboutOpen(true)}>О нас</button>
      </div>
      <AboutModal open={aboutOpen} onClose={() => setAboutOpen(false)} />
    </header>
  );
}

export default Header;
