import React, { useCallback, useState } from 'react';
import '../styles/LayersPanel.css';

function LayersPanel({ elements, selectedElementIds, setSelectedElementIds, updateElement, updateElementProps, onMoveLayer }) {
  const [editingId, setEditingId] = useState(null);
  const [draggedItem, setDraggedItem] = useState(null);

  // Мультивыделение слоёв
  const handleSelect = useCallback((e, id) => {
    if (e.shiftKey || e.ctrlKey || e.metaKey) {
      setSelectedElementIds(prev => {
        if (prev.includes(id)) {
          return prev.filter(elId => elId !== id);
        } else {
          return [...prev, id];
        }
      });
    } else {
      setSelectedElementIds([id]);
    }
  }, [setSelectedElementIds]);

  const handleNameChange = useCallback((e, id) => {
    updateElementProps(id, { name: e.target.value });
  }, [updateElementProps]);

  const handleDoubleClick = useCallback((id) => {
    setEditingId(id);
  }, []);

  const handleBlur = useCallback(() => {
    setEditingId(null);
  }, []);

  const getElementTypeIcon = (type) => {
    switch (type) {
      case 'text': return <i className="fas fa-font"></i>;
      case 'image': return <i className="fas fa-image"></i>;
      case 'button': return <i className="fas fa-square-full"></i>;
      case 'shape': return <i className="fas fa-shapes"></i>;
      default: return <i className="fas fa-cube"></i>;
    }
  };

  // Drag & Drop для изменения порядка слоёв
  const handleDragStart = useCallback((e, index) => {
    setDraggedItem(elements[index]);
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/plain', index.toString());
  }, [elements]);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  }, []);

  const handleDrop = useCallback((e, dropIndex) => {
    e.preventDefault();
    const draggedIndex = parseInt(e.dataTransfer.getData('text/plain'));
    if (draggedItem && draggedIndex !== dropIndex) {
      onMoveLayer(draggedIndex, dropIndex);
    }
    setDraggedItem(null);
  }, [draggedItem, onMoveLayer]);

  const handleDragEnd = useCallback(() => {
    setDraggedItem(null);
  }, []);

  // reverse для отображения верхних слоёв сверху
  const sortedElements = [...elements].reverse();

  return (
    <div className="layers-panel">
      <h3><i className="fas fa-layer-group"></i> Слои ({elements.length})</h3>
      <div className="layers-list">
        {sortedElements.length === 0 && <p className="no-layers">Нет элементов на канвасе.</p>}
        {sortedElements.map((el, index) => {
          const originalIndex = elements.findIndex(item => item.id === el.id);
          const isSelected = selectedElementIds.includes(el.id);
          return (
            <div
              key={el.id}
              className={`layer-item${isSelected ? ' selected' : ''}${draggedItem && draggedItem.id === el.id ? ' dragging' : ''}`}
              onClick={e => handleSelect(e, el.id)}
              onDoubleClick={() => handleDoubleClick(el.id)}
              draggable="true"
              onDragStart={e => handleDragStart(e, originalIndex)}
              onDragOver={handleDragOver}
              onDrop={e => handleDrop(e, originalIndex)}
              onDragEnd={handleDragEnd}
            >
              <div className="layer-handle"><i className="fas fa-grip-lines"></i></div>
              <div className="layer-type-icon">{getElementTypeIcon(el.type)}</div>
              {editingId === el.id ? (
                <input
                  type="text"
                  value={el.props.name || `${el.type} ${el.id.substring(0, 4)}`}
                  onChange={e => handleNameChange(e, el.id)}
                  onBlur={handleBlur}
                  autoFocus
                  onFocus={e => e.target.select()}
                />
              ) : (
                <span className="layer-name">
                  {el.props.name || `${el.type} ${el.id.substring(0, 4)}`}
                </span>
              )}
              <div className="layer-actions">
                <button className="layer-action-btn" title="Скрыть/Показать (пока не работает)"><i className="fas fa-eye"></i></button>
                <button className="layer-action-btn" title="Блокировать/Разблокировать (пока не работает)"><i className="fas fa-lock"></i></button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default LayersPanel;
